import React, { useState } from "react";
// import Sound from "react-sound";
import { motion } from "framer-motion";
import styles from "./styles.module.sass";
import pickles from "./images/pickles.jpg";
import toki from "./images/toki.jpg";
import griffins from "./images/griffins.jpg";

const cardVariants = {
  flipped: {
    rotateY: 180,
    transition: { duration: 0.5 },
    boxShadow:
      "rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px",
  },
  notFlipped: () => ({
    rotateY: 0,
    boxShadow:
      "rgba(0, 0, 0, 0.1) 0px 4px 6px -1px, rgba(0, 0, 0, 0.06) 0px 2px 4px -1px",
    transition: { duration: 0.5 },
  }),
};
const Card = () => {
  const [fading, setFading] = useState(false);
  const [flipped, setFlipped] = useState(false);

  const fadeFunction = () => {
    setFading(!fading);
  };

  const handleClick = () => {
    setFlipped(!flipped);
  };

  return (
    <>
      <div role="button" tabIndex={0} className={styles.flashcards}>
        <div>
          <motion.div
            className={styles.card}
            onClick={() => handleClick()}
            variants={cardVariants}
            animate={flipped ? "flipped" : "notFlipped"}
          >
            <motion.div
              className={
                flipped ? `${styles.backContent}` : `${styles.frontContent}`
              }
              onMouseDown={() => {
                fadeFunction();
              }}
              onMouseUp={() => {
                setTimeout(() => {
                  fadeFunction();
                }, 500);
              }}
              animate={{ opacity: fading ? 0 : 1 }}
            >
              <div
                className={fading || flipped ? "is-hidden" : styles.imageArea}
              >
                <div className={styles.topImage}>
                  <img src={griffins} alt="Quinn and Hayes"></img>
                </div>
                <div className={styles.bottomImages}>
                  <img src={pickles} alt="Pickles the cat."></img>
                  <img src={toki} alt="Toki the cat."></img>
                </div>
              </div>
              <div
                className={fading || !flipped ? "is-hidden" : styles.message}
              >
                <h1>Happy Holidays from the Griffins</h1>
                <p>
                  2021 saw the Griffins move back to Columbus, OH! We are very
                  excited to be closer to our family and friends.
                </p>
                <p>
                  Hayes has been hard at work on international online music
                  school{" "}
                  <a href="https://www.mandolinsecrets.com/">
                    Mandolin Secrets
                  </a>{" "}
                  and recently launched the Guitar Club section of the site. He
                  is also now teaching in the{" "}
                  <a href="https://denison.edu/people/hayes-griffin">
                    American Roots program at Denison University
                  </a>
                  .
                </p>
                <p>
                  Quinn completed the{" "}
                  <a href="https://www.shecodes.io/stories/Quinn-junior-react-developer">
                    SheCodes workshop
                  </a>{" "}
                  in fall 2021 and transitioned into a web development career in
                  May.
                </p>
              </div>
            </motion.div>
          </motion.div>
        </div>
        <p className={styles.clickNote}>
          Click the card for a holiday message from Quinn and Hayes.
        </p>
      </div>
    </>
  );
};

export default Card;
