import React from "react";
import Sound from "react-sound";
import { motion } from "framer-motion";
import styles from "./styles.module.sass";

const cardVariants = {
  flipped: {
    rotateY: 180,
    transition: { duration: 0.5 },
    boxShadow:
      "rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px",
  },
  notFlipped: () => ({
    rotateY: 0,
    boxShadow:
      "rgba(0, 0, 0, 0.1) 0px 4px 6px -1px, rgba(0, 0, 0, 0.06) 0px 2px 4px -1px",
    transition: { duration: 0.5 },
  }),
};
const Card = () => {
  const [fading, setFading] = useState(false);
  const [flipped, setFlipped] = useState(false);

  const fadeFunction = () => {
    setFading(!fading);
  };

  const handleClick = () => {
    setFlipped(!flipped);
  };

  return (
    <>
      <div className={styles.cardContainer}>
        <div
          className={flipped && !fading ? styles.cardTopBack : styles.cardTop}
        >
          <button
            className={`${styles.favButton} button is-ghost ${
              fading ? "is-invisible" : ""
            }`}
            type="button"
            onClick={() => {
              cardSet[cardNumber].favorite = !cardSet[cardNumber].favorite;
              setCardSet([...cardSet]);
              localStorage.setItem("cardSet", JSON.stringify(cardSet));
            }}
          >
            {cardSet[cardNumber].favorite ? <FaStar /> : <FaRegStar />}
          </button>
          <button
            type="button"
            className={
              fading || !cardFlipped
                ? "is-hidden"
                : `button is-danger ${styles.exampleButton}`
            }
            onClick={() => {
              setExampleMode(!exampleMode);
              const exampleDiv = document.getElementById("example-text");
              exampleDiv.innerHTML = BoldTextHelper(
                currentCard.term,
                currentCard.example
              );
            }}
          >
            Example
          </button>
        </div>
        <div role="button" tabIndex={0} className={styles.flashcards}>
          <div>
            <motion.div
              className={styles.card}
              key={currentCard.term}
              onClick={() => handleClick(currentCard)}
              variants={cardVariants}
              animate={cardFlipped ? "flipped" : "notFlipped"}
            >
              <motion.div
                className={
                  cardFlipped
                    ? `${styles.backContent}`
                    : `${styles.frontContent}`
                }
                onMouseDown={() => {
                  fadeFunction();
                }}
                onMouseUp={() => {
                  setTimeout(() => {
                    fadeFunction();
                  }, 500);
                }}
                animate={{ opacity: fading ? 0 : 1 }}
              >
                <div className={fading ? "is-hidden" : ""}>
                  {cardFlipped ? currentCard.definition : currentCard.term}
                </div>
                <div
                  className={
                    fading || !cardFlipped
                      ? "is-hidden"
                      : `${styles.imageContainer}`
                  }
                >
                  <img
                    className={styles.cardImage}
                    src={`images/${currentCard.image}.jpg`}
                    alt={currentCard.term}
                  />
                </div>
              </motion.div>
            </motion.div>
          </div>
        </div>
      </div>
      <div
        className={exampleMode ? "modal is-active has-text-centered" : "modal"}
      >
        <div className="modal-background" />
        <div
          className={`modal-content ${styles.exampleText}`}
          id="example-text"
        >
          Example Loading
        </div>
        <Sound
          url={`/media/${modalAudio}`}
          playStatus={exampleMode ? "PLAYING" : "STOPPED"}
        />
        <button
          type="button"
          className="modal-close is-large"
          aria-label="close"
          onClick={() => {
            setExampleMode(!exampleMode);
          }}
        />
      </div>
    </>
  );
};

export default Card;
